/* For definitions of ZMG_STATE, ZMG_SEED_t, ZMG_U32, ZMG_SEED, see zmg.h */
/* IEEE 754 floating point constants, used for type-punning */
#define F_ONE_MSB 0x3f800000
#define F_MANTISSA 0x7fffff

float zmgf(ZMG_STATE *rng_p) {
	uint32_t u, r, s;
	union {
		float r;
		uint32_t i;
	} x, y, z;
	float *p, mx;

	u = ZMG_U32(rng_p);
	r = u & ZMG_RMASK;                       /* generate a rectangle id  */
	s = (u & (1 << ZMG_MR)) << (31-ZMG_MR);  /* generate an msb sign     */
	if (r<ZMG_R) {
		/* insist on at least 23 bits of randomness for the multiplier  */
#if ZMG_MR < 9
		/* assume rectangle is scaled by 2^(ZMG_MR-31) */
		x.r = (float)(u>>(ZMG_MR+1))*ZMG_Rect[r];
#else
		/* assume rectangle is scaled by 2^(-32) */
		x.r = (float)ZMG_U32(rng_p)*ZMG_Rect[r];
#endif
	} else {
		u >>= ZMG_MR+1;
		r = u & ZMG_TMASK;  /* use alias-urn method to select a left-over */
		if (r >= ZMG_T) {   /* in dummy-symbol range, restprob = 0 */
			r=ZMG_jump[r];
		} else if (ZMG_U32(rng_p)  > ZMG_restprob[r]) {
			r=ZMG_jump[r];
		}
		if (r < ZMG_T-1) { /* sample from a trapezoid */
			p = ZMG_Trap + 6*r;
			/* The 6 trapezoid parameters are as follows:
			   p[0] = width (dx)
			   p[1] = left height + right height (dy)
			   p[2] = slope of top trapezoid line
			   p[3] = xshift (x coordinate of midpoint of top line)
			   p[4] = yshift (y coordinate of midpoint of top line)
			   p[5] = epsilon (dist. from trapez-top to sure-accept region) */
			do {
				/* ZMG_U32 is called twice, keeping 23 bits each time,
				   but perhaps 16 bit resolution is enough? */
				x.i = (ZMG_U32(rng_p) & F_MANTISSA) ^ F_ONE_MSB;  /* ~Unif[1,2) */
				x.r = (x.r - 1.5f) * p[0];  /* ~Unif[-dx/2,dx/2) */
				y.i = (ZMG_U32(rng_p) & F_MANTISSA) ^ F_ONE_MSB;  /* ~Unif[1,2) */
				y.r = (y.r - 1.5f) * p[1];  /* ~Unif[-dy/2,dy/2) */
				mx = p[2]*x.r;
				if (y.r > mx) {
					x.r = -x.r;
					y.r = -y.r;
				}
				x.r += p[3];
				if (y.r <= mx-p[5]) { /* in quick accept region? */
					break;
				}
				y.r += p[4];
			} while (y.r > expf(-0.5f*x.r*x.r));
		} else { /* sample from the tail */
			do {
				x.i = (ZMG_U32(rng_p) & F_MANTISSA) ^ F_ONE_MSB; /* ~Unif[1,2) */
				x.r = ZMG_A + ZMG_K*logf(2.0f-x.r);
				y.r = x.r - ZMG_LAMBDA;
				z.i = (ZMG_U32(rng_p) & F_MANTISSA) ^ F_ONE_MSB;  /* ~Unif[1,2) */
			} while (z.r >= 1.0f + expf(-0.5f*y.r*y.r));
		}
	}
	x.i ^= s; /* attach a sign bit */
	return x.r;
}

void seedzmgf(ZMG_STATE *rng_p) {
	ZMG_SEED_t seed;
	union {
		float r;
		uint32_t i;
	} x;
	x.i = F_ONE_MSB;
	if (x.r != 1.0f) {
		fprintf(stderr,
			"Floating point representation incompatible with zmgf -- sorry.\n");
		exit(1);
	}
	if ((getentropy(&seed,sizeof(ZMG_SEED_t)) != 0)) {
		fprintf(stderr,"Failed to get entropy to seed random number generator.\n");
		exit(1);
	}
	ZMG_SEED(rng_p,seed);
}
